import React, { useState } from 'react';

const AdminDashboard = () => {
  const [form, setForm] = useState({
    username: '',
    password: '',
    specializare: ''
  });
  const [message, setMessage] = useState('');

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setMessage('');
    try {
      const response = await fetch('http://localhost:4000/api/admin/add-doctor', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          adminEmail: 'admin@example.com', // Poți lua din context/sesiune dacă ai autentificare reală
          username: form.username,
          password: form.password,
          specializare: form.specializare
        })
      });
      const data = await response.json();
      if (response.ok) {
        setMessage('Medicul a fost adăugat cu succes!');
        setForm({ username: '', password: '', specializare: '' });
      } else {
        setMessage(data.error || 'Eroare la adăugare!');
      }
    } catch (err) {
      setMessage('Eroare la conectare cu serverul!');
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: '40px auto', padding: 24, background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #eee' }}>
      <h2>Adaugă Medic Nou</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: 12 }}>
          <label>Email medic:</label>
          <input
            type="email"
            name="username"
            value={form.username}
            onChange={handleChange}
            required
            style={{ width: '100%', padding: 8, marginTop: 4 }}
          />
        </div>
        <div style={{ marginBottom: 12 }}>
          <label>Parolă:</label>
          <input
            type="password"
            name="password"
            value={form.password}
            onChange={handleChange}
            required
            minLength={8}
            style={{ width: '100%', padding: 8, marginTop: 4 }}
          />
        </div>
        <div style={{ marginBottom: 12 }}>
          <label>Specializare:</label>
          <input
            type="text"
            name="specializare"
            value={form.specializare}
            onChange={handleChange}
            required
            style={{ width: '100%', padding: 8, marginTop: 4 }}
          />
        </div>
        <button type="submit" style={{ width: '100%', padding: 10, background: '#007bff', color: '#fff', border: 'none', borderRadius: 4 }}>
          Adaugă Medic
        </button>
      </form>
      {message && <div style={{ marginTop: 16, color: message.includes('succes') ? 'green' : 'red' }}>{message}</div>}
    </div>
  );
};

export default AdminDashboard;
