import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '../styles/Login.css';
import logo from '../assets/logo.svg';

const Login = ({ isPatient }) => {
  const navigate = useNavigate();
  const [userType, setUserType] = useState('');
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');

  const handleUserTypeSelect = (type) => {
    setUserType(type);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // În funcție de tipul de utilizator, redirectăm către dashboard-ul corespunzător
    if (userType === 'doctor') {
      navigate('/doctor');
    } else if (userType === 'patient') {
      navigate('/patient');
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="logo">
          <img src={logo} alt="SenCare" />
          <h1>SenCare</h1>
        </div>

        <div className="user-type-selector">
          <button
            className={`user-type-btn ${userType === 'doctor' ? 'active' : ''}`}
            onClick={() => handleUserTypeSelect('doctor')}
          >
            <i className="fas fa-user-md"></i>
            Doctor
          </button>
          <button
            className={`user-type-btn ${userType === 'patient' ? 'active' : ''}`}
            onClick={() => handleUserTypeSelect('patient')}
          >
            <i className="fas fa-user"></i>
            Patient
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="login-button">
            Log In
          </button>
        </form>

        <div className="forgot-password">
          <a href="/forgot-password">Forgot password?</a>
        </div>

        {!isPatient && (
          <div className="signup-link">
            Nu ai cont? <Link to="/signup">Creează cont</Link>
          </div>
        )}
      </div>
    </div>
  );
};

export default Login;
