import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ReactComponent as PatientIcon } from '../assets/patient-icon.svg';
import { ReactComponent as DoctorIcon } from '../assets/doctor-icon.svg';
import './RoleSelection.css';

const RoleSelection = () => {
  const navigate = useNavigate();
  const [selectedRole, setSelectedRole] = useState(null);

  if (selectedRole === 'doctor') {
    return (
      <div className="role-selection-container">
        <button className="role-btn" style={{ cursor: 'default' }}>
          <DoctorIcon style={{ width: 32, height: 32, marginRight: 8, verticalAlign: 'middle' }} />
          <span className="role-text">Doctor</span>
        </button>
        <div style={{ marginTop: 32 }}>
          <button className="role-btn" onClick={() => navigate('/medic')}>Continuă</button>
        </div>
      </div>
    );
  }
  if (selectedRole === 'pacient') {
    return (
      <div className="role-selection-container">
        <button className="role-btn" style={{ cursor: 'default' }}>
          <PatientIcon style={{ width: 32, height: 32, marginRight: 8, verticalAlign: 'middle' }} />
          <span className="role-text">Pacient</span>
        </button>
        <div style={{ marginTop: 32 }}>
          <button className="role-btn" onClick={() => navigate('/pacient')}>Continuă</button>
        </div>
      </div>
    );
  }

  return (
    <div className="role-selection-container">
      <h1>Bine ai venit la SenCare!</h1>
      <p>Alege tipul de utilizator:</p>
      <div className="role-buttons">
        <button onClick={() => setSelectedRole('doctor')} className="role-btn">
          <DoctorIcon style={{ width: 32, height: 32, marginRight: 8, verticalAlign: 'middle' }} />
          <span className="role-text">Doctor</span>
        </button>
        <button onClick={() => setSelectedRole('pacient')} className="role-btn">
          <PatientIcon style={{ width: 32, height: 32, marginRight: 8, verticalAlign: 'middle' }} />
          <span className="role-text">Pacient</span>
        </button>
      </div>
    </div>
  );
};

export default RoleSelection;
