import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '../styles/Login.css';
import logo from '../assets/logo.jpg';
import { ReactComponent as PatientIcon } from '../assets/patient-icon.svg';
import { ReactComponent as DoctorIcon } from '../assets/doctor-icon.svg';

const Login = ({ isPatient }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // În funcție de tipul de utilizator, redirectăm către dashboard-ul corespunzător
    if (isPatient) {
      navigate('/patient');
    } else {
      navigate('/doctor');
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="logo">
          <img src={logo} alt="SenCare" style={{ width: 200, margin: '0 auto 32px', display: 'block', borderRadius: '50%' }} />
        </div>

        {!isPatient && (
          <div style={{ margin: '24px 0', textAlign: 'center' }}>
            <i className="fas fa-user-md" style={{ fontSize: 64, color: '#3182CE', marginBottom: 8 }}></i>
            <div style={{ fontSize: '1.3em', color: '#2d3748', marginTop: 8, fontWeight: 600 }}>Doctor</div>
          </div>
        )}
        {isPatient && (
          <div style={{ margin: '24px 0', textAlign: 'center' }}>
            <PatientIcon style={{ width: 64, height: 64, color: '#3182CE' }} />
          </div>
        )}

        <h2>{!isPatient && 'Autentificare Doctor'}</h2>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="login-button">
            Log In
          </button>
        </form>

        <div className="forgot-password">
          <a href="/forgot-password">Forgot password?</a>
        </div>

        {!isPatient && (
          <div className="signup-link">
            Nu ai cont? <Link to="/signup">Creează cont</Link>
          </div>
        )}
      </div>
    </div>
  );
};

export default Login;
