import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Login.css';
import logo from '../assets/logo.jpg';
import { sql } from '../../backend/db'; // Import sql

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // 1. Verifică dacă există utilizatorul în baza de date
      const result = await sql.query`
        SELECT * FROM Utilizatori 
        WHERE Username = ${formData.email} AND Password = ${formData.password}
      `;

      // 2. Verifică dacă s-a găsit un utilizator
      if (result.recordset.length > 0) {
        const user = result.recordset[0];

        // 3. Redirecționează în funcție de tipul utilizatorului
        if (user.UserType === 'Doctor') {
          navigate('/doctor');
        } else if (user.UserType === 'Pacient') {
          navigate('/patient');
        } else {
          // Tip de utilizator necunoscut
          alert('Tip de utilizator necunoscut!');
        }
      } else {
        // Utilizator sau parolă incorectă
        alert('Email sau parolă incorectă!');
      }
    } catch (error) {
      console.error('Eroare la autentificare:', error);
      alert('Eroare la autentificare!');
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="logo">
          <img
            src={logo}
            alt="SenCare"
            style={{
              width: 200,
              margin: '0 auto 32px',
              display: 'block',
              borderRadius: '50%'
            }}
          />
        </div>

        {/* Pictograma de doctor */}
        <div style={{ margin: '24px 0', textAlign: 'center' }}>
          <i className="fas fa-user-md" style={{ fontSize: 64, color: '#3182CE', marginBottom: 8 }}></i>
        </div>

        <h2>Autentificare</h2>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              name="password"
              placeholder="Parolă"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="login-button">
            Autentificare
          </button>
        </form>

        <div className="forgot-password">
          <a href="/forgot-password">Ai uitat parola?</a>
        </div>
      </div>
    </div>
  );
};

export default Login;
