import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Login.css';
import logo from '../assets/logo.jpg';

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Logica pentru autentificare
    if (formData.email.includes('doctor')) {
      navigate('/doctor'); // Redirecționează doctorii
    } else {
      navigate('/patient'); // Redirecționează pacienții
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="logo">
          <img
            src={logo}
            alt="SenCare"
            style={{
              width: 200,
              margin: '0 auto 32px',
              display: 'block',
              borderRadius: '50%'
            }}
          />
        </div>

        {/* Pictograma de doctor */}
        <div style={{ margin: '24px 0', textAlign: 'center' }}>
          <i className="fas fa-user-md" style={{ fontSize: 64, color: '#3182CE', marginBottom: 8 }}></i>
        </div>

        <h2>Autentificare</h2>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              name="password"
              placeholder="Parolă"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="login-button">
            Autentificare
          </button>
        </form>

        <div className="forgot-password">
          <a href="/forgot-password">Ai uitat parola?</a>
        </div>
      </div>
    </div>
  );
};

export default Login;
